  private val timeSliceInDays = 365
  private val tennisDBN = GenericTennisDBN(priorProb, emissionProb, transitionProb, timeSliceInDays)

  def matchProb(playerA: String, playerB: String, timestamp: Long): Option[Double] = {
    println("Predicting winner of tennis match  (DBN model)")

    val result = MatchOutcome(playerA, playerB, None, timestamp)
    tennisDBN.addMatch(result)

    tennisDBN.calibrate(iter => println("LoopyBP iter=" + iter))

    val matchProbAGivenB = tennisDBN.getMatchProbability(result.playerA, result.playerB, result.matchTime)
    Some(matchProbAGivenB)
  }

  def addMatchResult(matchComposite: MatchComposite) {

    val result = toMatchOutcome(matchComposite)
    try {
      tennisDBN.updateMatch(result)
    } catch {
      case e: NoSuchElementException => tennisDBN.addMatch(result)
    }
  }

  private def toMatchOutcome(m: MatchComposite): MatchOutcome = {

    val playerAName = m.matchFacts.playerAFacts.playerName
    val playerBName = m.matchFacts.playerBFacts.playerName
    val playerAWinner = m.matchFacts.winner.equals(m.matchFacts.playerAFacts.playerName)

    MatchOutcome(playerAName, playerBName, playerAWinner, m.tournament.tournamentTime.getTime)
  }
}